import styles from 'styled-components';


export const Section = styles.section`
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
    padding: 0 2.4rem;
    margin: 9rem 0;
    gap: 2.5rem;
`;

export const IntroTitle = styles.h1`
   font-size: 2.45rem;
   color: hsl(192, 100%, 9%);
   font-weight: 700;
   line-height: 1.5;
`;

export const IntroPara = styles.p`
   font-size: 1.45rem;
   color: hsl(192, 100%, 9%);
   line-height: 1.5;
`;

export const IntroButton = styles.button`
    width: 65%;
    align-self: center;
    height: 4rem;
    border-radius: 25px;
    border: none;
    background-color:hsl(322, 100%, 66%);
    color: white;
    font-weight: 700;
    font-size: 1.25rem;
    margin-top: 2.6rem;
`;
