import styles from 'styled-components';

export const Footer = styles.footer`
`;

export const FooterWrapper = styles.div`
  display: flex;
  flex-direction: ${props => props.Display};
  background-color: ${props => props.BGColor};
  padding: 4.25rem 2.6rem;
  padding-top: 6rem;
`;

export const ContactWrapper = styles.div`
  display: flex;
  flex-direction: ${props => props.Display};
  background-color: ${props => props.BGColor};
  color: hsl(207, 100%, 98%);
  gap: 1.6rem;
  margin-top: 10rem;
`;

export const SocialWrapper = styles.div`
  display: flex;
  flex-direction: ${props => props.Display};
  background-color: ${props => props.BGColor};
  color: hsl(207, 100%, 98%);
  gap: 1.6rem;

  & a {
     color: hsl(210, 100%, 98%);
  }

  & a i {
     font-size: 2.75rem;
  }
`;


export const FooterWrapperChild = styles.div`
 display: flex;
 flex-direction: ${props => props.Display};
`;

export const ContactWrapperChild = styles.div`
 display: flex;
 flex-direction: ${props => props.Display};
`;

export const CompanyContact = styles.div`
 display: flex;
 flex-direction: ${props => props.Display};
 margin: 3.2rem 0;
 gap: 1.75rem;

 & address {
    display: flex;
    gap: 1.75rem;
    align-items: center;
 }

 & address a {
   color: hsl(210, 100%, 98%);
   text-decoration: none;
   font-style: normal;
   font-size: 1.5rem;
 }
`;


export const NewsLetterWrapper = styles.div`
 display: flex;
 flex-direction: ${props => props.Display};
 color: hsl(207, 100%, 98%);
 gap: 2.4rem;
`;


export const FooterParagraph = styles.p`
font-size: 1.5rem;
font-weight: 400;
line-height: 1.6;
`;

export const FooterHeading = styles.p`
   font-size: 2rem;
   text-transform: uppercase;
   font-weight: 700;

`;

export const Image = styles.img.attrs(props => ({
   src: props.Src,
   alt: props.Alt
}))`
   display: block;
   margin-bottom: -5px;
`;


export const FooterImage = styles.img.attrs(props => ({
   src: props.Src,
   alt: props.Alt
}))`
   display: inline-block;
   width: 20rem;
`;


export const ImageIcon = styles.img.attrs(props => ({
   src: props.Src,
   alt: props.Alt
}))`
   display: inline-block;
   width: 2rem;
   height: 1.8rem;
`;

export const Input = styles.input.attrs(props => ({
   type: props.Type,
   value: props.Value
}))`
   height: 5rem;
   border-radius: 6px;
   border: none;

   &:nth-child(odd){
      font-size: 1.5rem;
      padding: 0 1.6rem;
      outline: none;
   }

   &:nth-child(even) {
      display: inline-block;
      width: 50%;
      align-self: flex-end;
      background-color: hsl(322, 100%, 66%);
      color: hsl(207, 100%, 98%);
      font-weight: 700;
      font-size: 1.6rem;
   }
`;


export const Form = styles.form.attrs(props => ({
   action: props.Action
}))`
   display: flex;
   flex-direction: column;
   margin-top: 1.25rem;
   gap: 1.6rem;
`;


export const Address = styles.address`
   display: flex;
`;
