import styles from 'styled-components';


export const InsightImage = styles.img.attrs( props=>({
    src: props.Src,
    alt: props.Alt
}))`
    display: inline-block;
    width: 95%;
    margin: auto;
    margin-top: 1rem;
`;

export const InsightImageIcon = styles.img.attrs( props=>({
    src: props.Src,
    alt: props.Alt
}))`
    display: inline-block;
    height: 1.8rem;
    width: 2.25rem;
`;


export const InsightDisplay = styles.div`
    display: flex;
    flex-direction: ${props=>props.Display};
`;

export const InsightFlex = styles.div`
    display: flex;
    flex-direction: column;
    align-self: center;
    width: max-content;
    & p {
        font-size: 5.25rem;
        font-weight: 700;
        line-height: 1.5;
        text-align: center;
        color: hsl(192, 100%, 9%);
    }

    & p span {
        display: block;
        font-size: 1.25rem;
        margin-left: -1rem;
        opacity: 0.3;
    }
`;
