import styles from 'styled-components';
import { device } from './Devices/devices';


export const InsightImage = styles.img.attrs(props => ({
    src: props.Src,
    alt: props.Alt
}))`
    display: inline-block;
    width: 100%;
    margin: auto;
    margin-top: 1rem;

    @media ${device.mobileM}{
        margin-top: 1rem;
    }
`;

export const InsightImageIcon = styles.img.attrs(props => ({
    src: props.Src,
    alt: props.Alt
}))`
    display: inline-block;
    height: 1.6rem;
    width: 2rem;

    @media ${device.mobileM}{
        height: 1.8rem;
        width: 2.25rem;
    }
`;


export const InsightDisplay = styles.div`
    display: flex;
    width: 90%;
    margin: auto;
    flex-direction: ${props => props.Display};
    gap: 10rem;

    @media ${device.mobileM}{
        width: 90%;
        margin: auto;
        flex-direction: ${props => props.Display};
        gap: 11rem;
    }
`;

export const InsightFlex = styles.div`
    display: flex;
    flex-direction: column;
    align-self: center;
    width: max-content;
    & p {
        font-size: 5.25rem;
        font-weight: 700;
        line-height: 1.5;
        text-align: center;
        color: hsl(192, 100%, 9%);
    }

    & p span {
        display: block;
        font-size: 1.25rem;
        margin-left: -1rem;
        opacity: 0.4;
    }

    .firstChildImage {
        height: 2rem;
        width: 2.6rem;
    }

    .firstChild {
        font-size: 6rem;
    }

    .firstChild span {
        font-size: 1.5rem;
        margin-left: 0;
    }

    @media ${device.mobileM}{
        & p {
            font-size: 5.25rem;
            font-weight: 700;
            line-height: 1.5;
            text-align: center;
            color: hsl(192, 100%, 9%);
        }

        & p span {
            display: block;
            font-size: 1.25rem;
            margin-left: -1rem;
            opacity: 0.4;
        }

        .firstChildImage {
            height: 2.6rem;
            width: 3rem;
        }
        .firstChild {
            font-size: 6rem;
        }

        .firstChild span {
            font-size: 1.5rem;
            margin-left: 0;
        }

    }
`;
