import styles from 'styled-components';



export const IllustrationImg = styles.img`
    display: inline-block;
    width: 100%;
    margin: auto;
    margin-top: 9rem;
`;


export const IllustrationSection = styles.section`
    display: block;
    text-align: center;
    align-items: center;
    margin-top: 1rem;
    color: hsl(192, 100%, 9%);
    & > img {
        margin-bottom: -10px;
    }

`;

export const TextWrapper = styles.div`
    width: 85%;
    display: flex;
    flex-direction: column;
    margin: auto;
    margin-top: 7rem;
    margin-bottom: 9rem;
    gap: 1.6rem;
`;


export const IllustrationArticle = styles.article`
    display: flex;
    flex-direction: column;
    .ArticleImg {
        width: 70%;
    }

`;


export const IllustrationWrapper = styles.div`
    & ${IllustrationArticle}:first-child {
        background-color: hsl(210, 100%, 98%);
    }

    & ${IllustrationArticle}:nth-last-child(2){
        background-color: hsl(210, 100%, 98%);
    }

    & > ${IllustrationImg}:nth-last-child(3){
        margin-bottom: -10px;
    }

    &:last-child ${TextWrapper} {
        margin
    }
`;


export const IllustrationHeading = styles.h2`
    font-family: 'Poppins', sans-serif;
    font-size: 2rem;
`;

export const IllustrationPara = styles.p`
    font-size: 1.4rem;
    font-weight: 400;
`;
